function device(value, param) {
  if (!list[param]) list[param] = {};
  if (value.mac) list[param]['mac']               = value.mac;
  if (value.mac) list[param]['model']             = value.model;
  if (value.mac) list[param]['powerSource']       = value.regulatory.powerSource;
